mapper.select:
SELECT
-- 级联表的字段 ,jingdian.jingdian_name as jingdianName ,jingdian.jingdian_types as jingdianTypes ,jingdian.jingdian_dengji_types as jingdianDengjiTypes ,jingdian.jingdian_photo as jingdianPhoto ,jingdian.jingdian_content as jingdianContent ,jingdian.jingdian_new_money as jingdianNewMoney ,yonghu.username as username ,yonghu.password as password ,yonghu.yonghu_name as yonghuName ,yonghu.sex_types as sexTypes ,yonghu.id_number as idNumber ,yonghu.phone as phone ,yonghu.yonghu_photo as yonghuPhoto
FROM jingdian_collection a left JOIN jingdian jingdian ON a.jingdian_id = jingdian.id left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id
and a.jingdian_id = #{params.jingdianId}
and a.yonghu_id = #{params.yonghuId}
and DATE_FORMAT(a.insert_time, '%Y-%m-%d %H:%T:%s') >= DATE_FORMAT(#{params.insertTimeStart}, '%Y-%m-%d %H:%T:%s')
and DATE_FORMAT(a.insert_time, '%Y-%m-%d %H:%T:%s') <= DATE_FORMAT(#{params.insertTimeEnd} , '%Y-%m-%d %H:%T:%s')
and jingdian.jingdian_name like CONCAT('%',#{params.jingdianName},'%')
and jingdian.jingdian_types = #{params.jingdianTypes}
and jingdian.jingdian_dengji_types = #{params.jingdianDengjiTypes}
and jingdian.jingdian_content like CONCAT('%',#{params.jingdianContent},'%')
and jingdian.jingdian_new_money >= #{params.jingdianNewMoneyStart}
and jingdian.jingdian_new_money <= #{params.jingdianNewMoneyEnd}
and yonghu.username like CONCAT('%',#{params.username},'%')
and yonghu.password like CONCAT('%',#{params.password},'%')
and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')
and yonghu.sex_types = #{params.sexTypes}
and yonghu.id_number like CONCAT('%',#{params.idNumber},'%')
and yonghu.phone like CONCAT('%',#{params.phone},'%')
order by a.${params.orderBy} desc
