mapper.select:
SELECT
-- 级联表的字段 ,luxian.luxian_name as luxianName ,luxian.luxian_photo as luxianPhoto ,luxian.luxian_content as luxianContent ,luxian.luxian_types as luxianTypes ,luxian.luxian_jingdian as luxianJingdian ,luxian.luxian_new_money as luxianNewMoney ,yonghu.username as username ,yonghu.password as password ,yonghu.yonghu_name as yonghuName ,yonghu.sex_types as sexTypes ,yonghu.id_number as idNumber ,yonghu.phone as phone ,yonghu.yonghu_photo as yonghuPhoto
FROM luxian_collection a left JOIN luxian luxian ON a.luxian_id = luxian.id left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id
and a.luxian_id = #{params.luxianId}
and a.yonghu_id = #{params.yonghuId}
and DATE_FORMAT(a.insert_time, '%Y-%m-%d %H:%T:%s') >= DATE_FORMAT(#{params.insertTimeStart}, '%Y-%m-%d %H:%T:%s')
and DATE_FORMAT(a.insert_time, '%Y-%m-%d %H:%T:%s') <= DATE_FORMAT(#{params.insertTimeEnd} , '%Y-%m-%d %H:%T:%s')
and luxian.luxian_name like CONCAT('%',#{params.luxianName},'%')
and luxian.luxian_content like CONCAT('%',#{params.luxianContent},'%')
and luxian.luxian_types = #{params.luxianTypes}
and luxian.luxian_jingdian like CONCAT('%',#{params.luxianJingdian},'%')
and luxian.luxian_new_money >= #{params.luxianNewMoneyStart}
and luxian.luxian_new_money <= #{params.luxianNewMoneyEnd}
and yonghu.username like CONCAT('%',#{params.username},'%')
and yonghu.password like CONCAT('%',#{params.password},'%')
and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')
and yonghu.sex_types = #{params.sexTypes}
and yonghu.id_number like CONCAT('%',#{params.idNumber},'%')
and yonghu.phone like CONCAT('%',#{params.phone},'%')
order by a.${params.orderBy} desc
